(function($) {
  $.fn.accordion_tab = function(options) {
    return this.each(function() {
      var settings = $.extend({
        firstChildExpand: true,
        multiExpand: false,
        slideSpeed: 500,
        dropDownIcon: '&#9660'
      }, options);
      $(this).children(".min_box-h_tab").each(function() {
        $(this).next('.min_box-tx_tab').andSelf().wrapAll("<div class='accordion-item_tab'></div>");
      });
      $(this).children().children('.min_box-tx_tab').addClass("accordion-content_tab");
      $(this).find(".min_box-h_tab").wrap("<div class='accordion-header_tab'></div>");
      $(this).find(".min_box-h_tab").after("<div class='accordion-header-icon_tab'>" + settings.dropDownIcon + "</div>");
      $(this).children('.accordion-item_tab').wrap('<div class="drawer_tab"></div>');
      if (settings.firstChildExpand == true) {
        $(this).find(".accordion-header_tab:first").toggleClass("accordion-header-active_tab");
        $(this).find(".accordion-header_tab:first").parent().toggleClass("accordion-item-active_tab");
        $(this).find(".accordion-header_tab:first").next().slideToggle(0);
        $(this).find(".accordion-header_tab:first").children(".accordion-header-icon_tab").toggleClass("accordion-header-icon-active_tab");
      }
      $(this).find(".accordion-header_tab").click(function() {
        if (settings.multiExpand == false) {
          if (!$(this).hasClass('accordion-header-active_tab')) {
            $(this).parent().parent().parent().find(".accordion-header-active_tab").removeClass('accordion-header-active_tab');
            $(this).parent().parent().parent().find(".accordion-item-active_tab").children(".accordion-content_tab").slideUp(settings.slideSpeed);
            $(this).parent().parent().parent().find(".accordion-header-icon-active_tab").removeClass("accordion-header-icon-active_tab");
            $(this).parent().parent().parent().find(".accordion-item-active_tab").removeClass("accordion-item-active_tab");
          }
        }
        $(this).toggleClass("accordion-header-active_tab");
        $(this).parent().toggleClass("accordion-item-active_tab");
        $(this).next().slideToggle(settings.slideSpeed);
        $(this).children(".accordion-header-icon_tab").toggleClass("accordion-header-icon-active_tab");
      });
    });
  }
  $.fn.openSection_tab = function(index) {
    $(".accordion_tab").find(".accordion-header_tab").filter(function(indexx) {
      return indexx == index;
    }).click();
  }
}(jQuery));




(function($) {
  $.fn.accordion = function(options) {
    return this.each(function() {
      var settings = $.extend({
        firstChildExpand: false,
        multiExpand: false,
        slideSpeed: 500,
        dropDownIcon: '&#9660'
      }, options);
      $(this).children(".min_box-h").each(function() {
        $(this).next('.min_box-tx').andSelf().wrapAll("<div class='accordion-item'></div>");
      });
      $(this).children().children('.min_box-tx').addClass("accordion-content");
      $(this).find(".min_box-h").wrap("<div class='accordion-header'></div>");
      $(this).find(".min_box-h").after("<div class='accordion-header-icon'>" + settings.dropDownIcon + "</div>");
      $(this).children('.accordion-item').wrap('<div class="drawer"></div>');
      if (settings.firstChildExpand == true) {
        $(this).find(".accordion-header:first").toggleClass("accordion-header-active");
        $(this).find(".accordion-header:first").parent().toggleClass("accordion-item-active");
        $(this).find(".accordion-header:first").next().slideToggle(0);
        $(this).find(".accordion-header:first").children(".accordion-header-icon").toggleClass("accordion-header-icon-active");
      }
      $(this).find(".accordion-header").click(function() {
        if (settings.multiExpand == false) {
          if (!$(this).hasClass('accordion-header-active')) {
            $(this).parent().parent().parent().find(".accordion-header-active").removeClass('accordion-header-active');
            $(this).parent().parent().parent().find(".accordion-item-active").children(".accordion-content").slideUp(settings.slideSpeed);
            $(this).parent().parent().parent().find(".accordion-header-icon-active").removeClass("accordion-header-icon-active");
            $(this).parent().parent().parent().find(".accordion-item-active").removeClass("accordion-item-active");
          }
        }
        $(this).toggleClass("accordion-header-active");
        $(this).parent().toggleClass("accordion-item-active");
        $(this).next().slideToggle(settings.slideSpeed);
        $(this).children(".accordion-header-icon").toggleClass("accordion-header-icon-active");
      });
    });
  }
  $.fn.openSection = function(index) {
    $(".accordion").find(".accordion-header").filter(function(indexx) {
      return indexx == index;
    }).click();
  }
}(jQuery));
